import { isUsingExperimentationPlatform } from './remoteConfiguration';
export default function shouldUseAddressCollectionSdk(country, resolver, hasAddressCollectionSdk) {
    if (!hasAddressCollectionSdk) {
        return false;
    }
    if (!isUsingExperimentationPlatform()) {
        return country === 'US';
    }
    return (country === 'US' && resolver.getBool('enable_address_collection_sdk_for_us'));
}
