'use strict';

var Promise = require('spotify-promise-plus');
var NOT_A_TOKEN = require('../../types/not_a_token');

function createEndpointsProvider() {
  return function() {
    return Promise.resolve({
      webgate: 'https://www.spotify.com/api/'
    });
  };
}

function createTokenProvider() {
  return function() {
    return Promise.resolve(NOT_A_TOKEN);
  };
}

module.exports = {
  createEndpointsProvider: createEndpointsProvider,
  createTokenProvider: createTokenProvider
};
