'use strict';

var _createTransport = require('spotify-transport');

function createTransport(options) {
  if (!options) {
    throw new TypeError('Argument `options` is required.');
  }
  if (!options.providers) {
    throw new TypeError('Argument `options` is required.');
  }

  return _createTransport({
    ownerRef: options.ownerRef,
    providers: options.providers,
    requestMode: options.requestMode,
    reconnectionRetries: options.reconnectionRetries,
    forcePolyfillTypes: options.forcePolyfillTypes
  });
}

module.exports = createTransport;
