'use strict';

/**
 * Represents a storage resolution error.
 *
 * @param {string} code The error code.
 * @param {string} message The error message.
 */
function FileError(code, message) {
  Error.call(this, message);

  this.code = code;

  /**
   * True if the error breaks the ability to play tracks ie. if the user has
   *     been play capped.
   *
   * @type {boolean}
   */
  this.canPlayNext = true;

  /**
   * Error message.
   *
   * @type {string}
   */
  this.message = message || 'File Error';

  this.debug = {};
}

// inherit from Native event.
FileError.prototype = new Error();
FileError.prototype.constructor = FileError;
FileError.prototype.name = 'FileError';

module.exports = FileError;
