'use strict';

function PlaybackOptions(overrides) {
  if (!(this instanceof PlaybackOptions)) {
    return new PlaybackOptions(overrides);
  }
  var options = overrides || {};
  this.index = 'index' in options ? options.index : -1;
  this.position = 'position' in options ? options.position : 0;
  this.initialPosition = 'initialPosition' in options ?
    options.initialPosition :
    0;
  this.duration = 'duration' in options ? options.duration : -1;
  this.paused = 'paused' in options ? options.paused : false;
  this.reason = options.reason || 'unknown';
}

PlaybackOptions.create = function(options) {
  if (options instanceof PlaybackOptions) {
    return options;
  }
  return new PlaybackOptions(options);
};

module.exports = PlaybackOptions;
