'use strict';

function TrackingError(code, message) {
  Error.call(this, message);

  /**
   * Error code.
   *
   * @type {string}
   * @see {module:spotify-playback/types/Errors}
   */
  this.code = code;

  /**
   * Error message string.
   *
   * @type {string}
   */
  this.message = message;

  /**
   * Object for storing properties of an event that's useful for debugging.
   *
   * @type {object}
   */
  this.debug = {};
}

TrackingError.prototype = new Error();
TrackingError.prototype.constructor = TrackingError;
TrackingError.prototype.name = 'TrackingError';

module.exports = TrackingError;
