'use strict';

function XResolveError(code, message, status) {
  Error.call(this, code);
  this.code = code;
  this.message = message;
  this.status = status || 0;
}

XResolveError.prototype = new Error();
XResolveError.prototype.constructor = XResolveError;
XResolveError.prototype.name = 'XResolveError';

module.exports = XResolveError;
